$(document).ready(function(){var t=$(window).width();$(".left-column").css("width",t),$(".right-column").css("width",t),$("#wrapper").css("width",2.05*t),$(".font-test").click(function(){$(this).next(".holder").slideToggle()}),$(".holder").click(function(){$("#wrapper").animate({marginLeft:"-="+t},500)}),$(".right-column").click(function(){$("#wrapper").animate({marginLeft:"+="+t},500)})});